import{_ as l,c as i,o as e,a}from"./app.bcd3b7af.js";const v=JSON.parse('{"title":"\u89C2\u4F17\u793E\u8BBA","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u5BF9\u5E94\u5C4A\u751F\u548C\u5F80\u5C4A\u751F\u533A\u522B\u5C31\u4E1A\u662F\u5426\u516C\u5E73\u7684\u4E00\u4E9B\u770B\u6CD5","slug":"\u5BF9\u5E94\u5C4A\u751F\u548C\u5F80\u5C4A\u751F\u533A\u522B\u5C31\u4E1A\u662F\u5426\u516C\u5E73\u7684\u4E00\u4E9B\u770B\u6CD5","link":"#\u5BF9\u5E94\u5C4A\u751F\u548C\u5F80\u5C4A\u751F\u533A\u522B\u5C31\u4E1A\u662F\u5426\u516C\u5E73\u7684\u4E00\u4E9B\u770B\u6CD5","children":[]},{"level":2,"title":"\u5173\u4E8E\u793E\u4F1A\u5316\u629A\u517B","slug":"\u5173\u4E8E\u793E\u4F1A\u5316\u629A\u517B","link":"#\u5173\u4E8E\u793E\u4F1A\u5316\u629A\u517B","children":[]},{"level":2,"title":"\u3010\u7761\u524D\u7701\u6D41\u7B2C\u4E00\u671F\u3011\u7701\u6D41\u603B\u7ED3\u4EE5\u53CA\u6211\u7684\u770B\u6CD5","slug":"\u3010\u7761\u524D\u7701\u6D41\u7B2C\u4E00\u671F\u3011\u7701\u6D41\u603B\u7ED3\u4EE5\u53CA\u6211\u7684\u770B\u6CD5","link":"#\u3010\u7761\u524D\u7701\u6D41\u7B2C\u4E00\u671F\u3011\u7701\u6D41\u603B\u7ED3\u4EE5\u53CA\u6211\u7684\u770B\u6CD5","children":[]},{"level":2,"title":"\u5E94\u8BD5\u4E4B\u6211\u89C1","slug":"\u5E94\u8BD5\u4E4B\u6211\u89C1","link":"#\u5E94\u8BD5\u4E4B\u6211\u89C1","children":[]}],"relativePath":"1/editorials.md"}'),t={name:"1/editorials.md"},s=a('<h1 id="\u89C2\u4F17\u793E\u8BBA" tabindex="-1">\u89C2\u4F17\u793E\u8BBA <a class="header-anchor" href="#\u89C2\u4F17\u793E\u8BBA" aria-hidden="true">#</a></h1><h2 id="\u5BF9\u5E94\u5C4A\u751F\u548C\u5F80\u5C4A\u751F\u533A\u522B\u5C31\u4E1A\u662F\u5426\u516C\u5E73\u7684\u4E00\u4E9B\u770B\u6CD5" tabindex="-1">\u5BF9\u5E94\u5C4A\u751F\u548C\u5F80\u5C4A\u751F\u533A\u522B\u5C31\u4E1A\u662F\u5426\u516C\u5E73\u7684\u4E00\u4E9B\u770B\u6CD5 <a class="header-anchor" href="#\u5BF9\u5E94\u5C4A\u751F\u548C\u5F80\u5C4A\u751F\u533A\u522B\u5C31\u4E1A\u662F\u5426\u516C\u5E73\u7684\u4E00\u4E9B\u770B\u6CD5" aria-hidden="true">#</a></h2><div class="info custom-block"><p class="custom-block-title">\u4F5C\u8005\u4FE1\u606F</p><ul><li>ID\uFF1A \u53C8\u9AD8\u53C8\u58EE\u7684\u97ED\u83DC</li><li>\u5E73\u53F0\uFF1AB\u7AD9</li><li>\u8054\u7CFB\u65B9\u5F0F\uFF1A15151855257</li></ul></div><p>\u6211\u89C9\u5F97\u7763\u5DE5\u5BF9\u6B64\u4E8B\u7684\u89C2\u70B9\u8BBA\u8BC1\u6709\u7455\u75B5\uFF0C\u6211\u4EEC\u56FD\u5BB6\u65E0\u8BBA\u662F\u516C\u52A1\u5458\u62DB\u6536\uFF0C\u56FD\u4F01\u62DB\u6536\uFF0C\u90FD\u5BF9\u5E94\u5C4A\u751F\u548C\u5F80\u5C4A\u751F\u6709\u6781\u5927\u7684\u533A\u5206\uFF0C\u5305\u62EC\u6BD5\u4E1A\u672A\u5C31\u4E1A\u6709\u4E24\u5E74\u7684\u5E94\u5C4A\u751F\u62E9\u4E1A\u671F\uFF0C\u4F46\u662F\u53EA\u8981\u4F60\u6709\u4E00\u4E2A\u6708\u7684\u793E\u4FDD\uFF0C\u4F60\u5C31\u7B97\u5F80\u5C4A\u751F\uFF0C\u518D\u52A0\u4E0A\u4F20\u95FB\u4E2D\u653F\u5E9C\u5BF9\u79C1\u4F01\u4E5F\u6709\u5E94\u5C4A\u751F\u7684\u8865\u8D34\uFF0C\u6240\u6709\u8FD9\u4E9B\u5145\u5206\u8BC1\u660E\uFF0C\u5E94\u5C4A\u751F\u5F80\u5C4A\u751F\u4E0D\u4EC5\u4EC5\u662F\u4E00\u4E2A\u5E02\u573A\u9009\u62E9\u7684\u95EE\u9898\uFF0C\u800C\u662F\u4E00\u4E2A\u653F\u5E9C\u7684\u653F\u7B56\u503E\u5411\u7684\u95EE\u9898\uFF0C\u9A6C\u524D\u5352\u7684\u8BBA\u8BC1\u4E1D\u6BEB\u6CA1\u6709\u6D89\u53CA\u5230\uFF0C\u6240\u4EE5\u8BF4\u8BBA\u8BC1\u6709\u7455\u75B5\u3002</p><p>\u53E6\u5916\u4E00\u70B9\uFF0C\u7763\u5DE5\u5728\u8C08\u8BBA\u8FD9\u4E2A\u8BDD\u9898\u65F6\uFF0C\u6240\u7528\u7684\u601D\u8DEF\u662F\uFF0C\u5E02\u573A\u7B5B\u9009\u51B3\u5B9A\u4E86\u503E\u5411\u5E94\u5C4A\u751F\uFF0C\u5B58\u5728\u5373\u5408\u7406\uFF0C\u7136\u800C\uFF0C\u7763\u5DE5\u81EA\u5DF1\u5728\u4F01\u4E1A\u6D89\u53CA\u6027\u522B\u6B67\u89C6\u65F6\uFF0C\u4E5F\u8BF4\u5230\u4E86\uFF0C\u6211\u4EEC\u5E94\u8BE5\u7ED9\u5973\u6027\u751F\u80B2\u8865\u507F\uFF0C\u653F\u5E9C\u76F4\u63A5\u7ED9\uFF0C\u800C\u4E0D\u662F\u4F01\u4E1A\u7ED9\uFF0C\u4EE5\u63D0\u9AD8\u4F01\u4E1A\u96C7\u4F63\u5973\u6027\u7684\u79EF\u6781\u6027\uFF0C\u6362\u53E5\u8BDD\u8BF4\uFF0C\u5E76\u4E0D\u662F\u5B58\u5728\u5373\u5408\u7406\uFF0C\u800C\u662F\u653F\u5E9C\u5E94\u8BE5\u901A\u8FC7\u653F\u7B56\uFF0C\u4FC3\u8FDB\u4E0D\u540C\u6027\u522B\u516C\u5E73\u5C31\u4E1A\uFF0C\u90A3\u4E48\u4E3A\u4EC0\u4E48\u4E0D\u540C\u6027\u522B\u533A\u522B\u5BF9\u5F85\u5E94\u8BE5\u7528\u653F\u7B56\u8FDB\u884C\u7EA0\u6B63\uFF0C\u5E94\u5C4A\u751F\u5F80\u5C4A\u751F\u5C31\u4E0D\u5E94\u8BE5\u7EA0\u6B63\uFF0C\u7763\u5DE5\u8FD9\u4E2A\u8BDD\u9898\u4EC5\u4EC5\u804A\u5230\u4E86\u8868\u5C42\u6CA1\u6709\u804A\u5230\u6839\u4E0A\u3002</p><div class="danger custom-block"><p class="custom-block-title">\u9A6C\u7763\u516C\u70B9\u8BC4</p><ol><li>\u6211\u6839\u672C\u4E0D\u8BA4\u4E3A\u6709\u5C31\u4E1A\u6B67\u89C6\uFF0C\u4F60\u81EA\u5DF1\u5F00\u4E2A\u4F01\u4E1A\uFF0C\u53EA\u4F1A\u9009\u6027\u4EF7\u6BD4\u6700\u597D\u7684\u52B3\u52A8\u529B\uFF0C\u4EE5\u53CA\u7B5B\u9009\u6210\u672C\u6700\u4F4E\u7684\u52B3\u52A8\u529B\u3002\u4E5F\u8BB8\u53CC\u975E\u7684\u6BD5\u4E1A\u751F\u4E5F\u6709\u4E0D\u9519\u7684\uFF0C\u4F46\u7B5B\u9009\u6210\u672C\u9AD8\uFF0C\u653F\u5E9C\u53C8\u4E0D\u5E2E\u52A9\u7B5B\u9009\uFF0C\u90A3\u53EA\u597D\u4E0D\u7528\u4E86\u3002</li><li>\u81F3\u4E8E\u56FD\u4F01\uFF0C\u9009\u5E94\u5C4A\u751F\u4E5F\u7B97\u5E2E\u52A9\u5B66\u751F\u83B7\u53D6\u5C31\u4E1A\u7ECF\u9A8C\u3002\u771F\u6B63\u7684\u95EE\u9898\u5728\u4E8E\u201C\u7EC8\u8EAB\u96C7\u4F63\u201D\uFF0C\u800C\u4E0D\u662F\u53EA\u8981\u5E94\u5C4A\u751F\u3002\u5982\u679C\u56FD\u4F01\u7528\u5DE5\u51CF\u5C11\u5C31\u76F4\u63A5n+1\u8F9E\u9000\uFF0C\u5C31\u6CA1\u8FD9\u4E48\u591A\u4EBA\u5728\u610F\u56FD\u4F01\u62DB\u8C01\u3002</li></ol></div><h2 id="\u5173\u4E8E\u793E\u4F1A\u5316\u629A\u517B" tabindex="-1">\u5173\u4E8E\u793E\u4F1A\u5316\u629A\u517B <a class="header-anchor" href="#\u5173\u4E8E\u793E\u4F1A\u5316\u629A\u517B" aria-hidden="true">#</a></h2><div class="info custom-block"><p class="custom-block-title">\u4F5C\u8005\u4FE1\u606F</p><ul><li>ID\uFF1A vidi_vici_veni</li><li>\u5E73\u53F0\uFF1AB\u7AD9</li><li>\u8054\u7CFB\u65B9\u5F0F\uFF1Aprion@live.de</li></ul></div><p>\u9A6C\u524D\u5352\u591A\u6B21\u5728\u8282\u76EE\u91CC\u63D0\u5230\u513F\u7AE5\u7684\u793E\u4F1A\u5316\u629A\u517B\uFF0C\u8FD9\u91CC\u6709\u51E0\u4E2A\u95EE\u9898\u9700\u8981\u6F84\u6E05\u3002\u90A3\u5C31\u662F\u793E\u4F1A\u5316\u629A\u517B\u7684\u513F\u7AE5\uFF0C\u5728\u672A\u6210\u5E74\u4E4B\u524D\uFF0C\u76D1\u62A4\u7684\u4E3B\u4F53\u662F\u8C01\u3002\u5982\u679C\u662F\u653F\u5E9C\u6216\u8005\u67D0\u79CD\u5F62\u5F0F\u7684\u516C\u6743\u529B\uFF08\u4E0B\u6587\u7B80\u79F0\u653F\u5E9C\uFF09\uFF0C\u4E8B\u5B9E\u4E0A\u653F\u5E9C\u548C\u7236\u6BCD\u5173\u7CFB\u5C31\u662F\u4E70\u5356\u513F\u7AE5\u7684\u5173\u7CFB\uFF0C\u7236\u6BCD\u751F\u4E0B\u6765\u4E00\u6B21\u4EA4\u6613\u57FA\u672C\u5C31\u5931\u53BB\u4E86\u5173\u7CFB\uFF08\u672A\u6765\u5668\u5B98\u6350\u8D60\u3001\u6362\u9AA8\u9AD3\u8FD9\u79CD\u6781\u7AEF\u751F\u7269\u5B66\u9700\u6C42\u9664\u5916\uFF09\u3002\u5728\u8FD9\u4E2A\u524D\u63D0\u4E0B\uFF0C\u76D1\u62A4\u4E3B\u4F53\u653F\u5E9C\u62E5\u6709\u5168\u90E8\u7684\u5904\u7F6E\u6743\u3002\u90A3\u4E48\u9A6C\u524D\u5352\u63D0\u5230\u7684\u5404\u79CD\u63AA\u65BD\u9664\u4E86\u8D22\u653F\u9700\u6C42\u548C\u6280\u672F\u7EC6\u8282\uFF0C\u6CA1\u6709\u5927\u7684\u963B\u529B\u3002\u513F\u7AE5\u672C\u8EAB\u5C31\u662F\u653F\u5E9C\u7684\uFF0C\u975E\u4E3B\u89C2\u6545\u610F\u7684\u517B\u6B7B\u4E86\uFF0C\u517B\u574F\u4E86\uFF0C\u4EC5\u4EC5\u662F\u56FD\u6709\u8D44\u4EA7\u6D41\u5931\u800C\u5DF2\u3002\u4F46\u662F\u8FD9\u4E2A\u65B9\u5F0F\uFF0C\u6709\u6BD4\u8F83\u5927\u7684\u98CE\u9669\u3002\u5728\u6CA1\u6709\u5F3A\u6709\u529B\u7684\u5BA3\u4F20\u63AA\u65BD\u4ECB\u5165\u4E0B\uFF0C\u80AF\u5411\u653F\u5E9C\u51FA\u552E\u5B50\u5973\u7684\u7236\u6BCD\uFF0C\u5B58\u5728\u7740\u4E00\u5B9A\u7684\u9053\u5FB7\u4F26\u7406\u538B\u529B\u3002\u4E8B\u5B9E\u4E0A\u53EF\u80FD\u662F\u793E\u4F1A\u7ECF\u6D4E\u5730\u4F4D\u8F83\u4F4E\u7684\u4EBA\u7FA4\uFF0C\u6362\u53E5\u8BDD\u8BF4\uFF0C\u662F\u7EDF\u8BA1\u5B66\u4E0A\u610F\u4E49\u4E0A\u57FA\u56E0\u6BD4\u8F83\u5DEE\u7684\u4EBA\u7FA4\u3002\u957F\u671F\u4E0B\u53BB\uFF0C\u4F1A\u4E0D\u65AD\u5F3A\u5316\uFF0C\u540E\u679C\u53EF\u80FD\u662F\u653F\u5E9C\u629A\u517B\u672C\u8EAB\u5C31\u6210\u4E86\u4E00\u4E2A\u4E8B\u5B9E\u4E0A\u7684\u4F4E\u7D20\u8D28\u6807\u7B7E\uFF0C\u957F\u8FDC\u770B\u4F1A\u5F15\u53D1\u793E\u4F1A\u95EE\u9898\u3002\u53E6\u4E00\u79CD\u5F62\u5F0F\uFF0C\u662F\u76D1\u62A4\u4E3B\u4F53\u8FD8\u662F\u7236\u6BCD\uFF0C\u653F\u5E9C\u662F\u517B\u80B2\u513F\u7AE5\u7684\u88AB\u59D4\u6258\u4EBA\uFF0C\u4EE3\u66FF\u7236\u6BCD\u884C\u4F7F\u90E8\u5206\u517B\u80B2\u4EFB\u52A1\u3002\u8FD9\u79CD\u6A21\u5F0F\u5B69\u5B50\u8FD8\u662F\u7236\u6BCD\u7684\uFF0C\u5728\u793E\u4F1A\u4F26\u7406\u9053\u5FB7\u4E0A\uFF0C\u63A5\u53D7\u5EA6\u6BD4\u524D\u4E00\u4E2A\u5F62\u5F0F\u8981\u63A5\u53D7\u5EA6\u9AD8\u5F97\u591A\u3002\u90A3\u4E48\u968F\u4E4B\u5E26\u6765\u7684\u4E00\u4E2A\u95EE\u9898\u662F\uFF0C\u7236\u6BCD\u5982\u4F55\u4FE1\u4EFB\u653F\u5E9C\u80FD\u628A\u5B69\u5B50\u517B\u597D\uFF1F\u6BD4\u5982\u73B0\u6709\u7684\u516C\u7ACB\u5E7C\u513F\u56ED\u8FD9\u79CD\u6A21\u5F0F\uFF0C\u7236\u6BCD\u6709\u6BD4\u8F83\u6DF1\u7684\u4ECB\u5165\uFF0C\u5927\u591A\u6570\u60C5\u51B5\u53EF\u4EE5\u76F8\u4FE1\u653F\u5E9C\u3002\u800C\u9A6C\u524D\u5352\u5021\u5BFC\u7684\u793E\u4F1A\u5316\u629A\u517B\uFF0C\u653F\u5E9C\u4ECB\u5165\u7A0B\u5EA6\u8981\u8FDC\u5927\u4E8E\u5E7C\u513F\u56ED\u3002\u7236\u6BCD\u8BA9\u6E21\u51FA\u7684\u6743\u529B\u8D23\u4EFB\uFF0C\u600E\u4E48\u76D1\u7763\u653F\u5E9C\uFF1F\u6211\u4EEC\u73B0\u5728\u6709\u4E00\u4E2A\u7C7B\u4F3C\u7684\u60C5\u5F62\uFF0C\u5C31\u662F\u517B\u8001\u9662\u3002\u5728\u76F8\u5F53\u7A0B\u5EA6\u4E0A\uFF0C\u517B\u8001\u9662\u662F\u548C\u9A6C\u524D\u5352\u7684\u793E\u4F1A\u5316\u629A\u517B\u59D4\u6258\u7A0B\u5EA6\u662F\u7C7B\u4F3C\u7684\uFF0C\u629A\u517B\u65B9\u8D1F\u8D23\u5927\u90E8\u5206\u7167\u987E\u8D23\u4EFB\u3002\u4F46\u662F\u76EE\u524D\u7684\u517B\u8001\u9662\uFF0C\u6216\u8005\u8BF4\u793E\u4F1A\u5316\u517B\u8001\u505A\u5F97\u975E\u5E38\u4E0D\u5982\u4EBA\u610F\u3002\u7EDF\u8BA1\u4E0A\u7684\u591A\u6570\uFF0C\u57FA\u672C\u4E0A\u628A\u8001\u4EBA\u9001\u5230\u517B\u8001\u9662\uFF0C\u5C5E\u4E8E\u4E0D\u5B5D\u884C\u4E3A\u3002\u76F8\u5BF9\u517B\u80B2\u513F\u7AE5\uFF0C\u517B\u80B2\u8001\u4EBA\u66F4\u5BB9\u6613\u76D1\u7763\uFF0C\u56E0\u4E3A\u8001\u4EBA\u662F\u6210\u5E74\u4EBA\uFF0C\u5927\u90E8\u5206\u6709\u4E00\u5B9A\u610F\u8BC6\u548C\u5224\u65AD\u529B\uFF0C\u53EF\u4EE5\u6BD4\u8F83\u6E05\u695A\u7684\u8BC4\u4F30\u81EA\u5DF1\u7684\u6EE1\u610F\u5EA6\u3002\u56E0\u6B64\uFF0C\u5982\u679C\u8BD5\u56FE\u8FDB\u884C\u540E\u4E00\u79CD\u65B9\u5F0F\u7684\u793E\u4F1A\u5316\u629A\u517B\u513F\u7AE5\uFF0C\u6211\u5EFA\u8BAE\u5148\u4ECE\u793E\u4F1A\u5316\u517B\u8001\u5F00\u59CB\uFF0C\u8FD9\u4E2A\u4E8B\u60C5\u529E\u597D\u4E86\uFF0C\u624D\u80FD\u53D6\u4FE1\u4E8E\u7FA4\u4F17\uFF0C\u529E\u597D\u793E\u4F1A\u5316\u629A\u517B\u513F\u7AE5\u3002\u800C\u4E14\u793E\u4F1A\u5316\u517B\u8001\u4E00\u6837\u6709\u91CD\u5927\u7684\u793E\u4F1A\u610F\u4E49\uFF0C\u96C6\u7EA6\u5316\u517B\u80B2\u53EF\u4EE5\u5927\u91CF\u7684\u8282\u7EA6\u793E\u4F1A\u8D44\u6E90\uFF0C\u5305\u62EC\u52B3\u52A8\u529B\u8D44\u6E90\u548C\u4F4F\u623F\u8D44\u6E90\u3002\u6BD4\u5982\u76F8\u5F53\u4E00\u90E8\u5206\u9AD8\u4EF7\u503C\u52B3\u52A8\u529B\uFF0C\u5F88\u591A\u4EBA\u51FA\u4E8E\u4E0D\u4FE1\u4EFB\u517B\u8001\u9662\u800C\u82B1\u8D39\u5927\u91CF\u65F6\u95F4\u81EA\u5DF1\u62C5\u8D1F\u8D77\u7801\u90E8\u5206\u7684\u517B\u8001\u4EFB\u52A1\u3002</p><p>\u5C0F\u7ED3\u4E00\u4E0B\uFF0C\u5411\u653F\u5E9C\u51FA\u552E\u513F\u7AE5\u5F0F\u793E\u4F1A\u5316\u629A\u517B\u76EE\u524D\u548C\u793E\u4F1A\u73B0\u5B9E\u5DEE\u8DDD\u8F83\u5927\uFF0C\u4E0D\u5408\u9002\u3002\u59D4\u6258\u653F\u5E9C\u629A\u517B\u7684\u6A21\u5F0F\u66F4\u73B0\u5B9E\u3002\u4F46\u662F\u540E\u8005\u8981\u6C42\u5148\u628A\u793E\u4F1A\u5316\u517B\u8001\u505A\u597D\uFF0C\u624D\u80FD\u53D6\u4FE1\u4E8E\u6C11\u3002</p><div class="danger custom-block"><p class="custom-block-title">\u9A6C\u7763\u516C\u70B9\u8BC4</p><p>\u53EA\u8BF4\u4E24\u4E2A\u95EE\u9898\u3002</p><ol><li>\u73B0\u5728\u5DF2\u7ECF\u662F\u201C\u7A77\u4EBA\u591A\u751F\uFF0C\u4E2D\u4EA7\u5C11\u751F\u201D\u4E86\uFF0C\u5982\u679C\u4F60\u771F\u89C9\u5F97\u9636\u5C42\u548C\u57FA\u56E0\u4F18\u52A3\u6709\u5173\u7CFB\uFF0C\u73B0\u5728\u7684\u4E16\u754C\u6B63\u5728\u98DE\u5954\u5411\u4F60\u60F3\u8C61\u7684\u5730\u72F1\u3002</li><li>\u793E\u4F1A\u5316\u629A\u517B\u7684\u672C\u8D28\uFF0C\u662F\u56FD\u5BB6\u76F4\u63A5\u4FDD\u62A4\u516C\u6C11\u2014\u2014\u65E0\u8BBA\u6210\u5E74\u548C\u672A\u6210\u5E74\u3002\u53EA\u8981\u7528\u8981\u6C42\u73B0\u5728\u516C\u7ACB\u5B66\u6821\u7684\u6807\u51C6\u53BB\u8861\u91CF\u7236\u6BCD\uFF0C\u793E\u4F1A\u5316\u629A\u517B\u5C31\u6210\u4E86\u4E00\u534A\u2014\u2014\u8001\u5E08\u6253\u5B66\u751F\u7559\u4E0B\u6DE4\u9752\u662F\u4EC0\u4E48\u60E9\u7F5A\uFF0C\u7236\u6BCD\u4E5F\u4E00\u6837\u3002</li></ol></div><h2 id="\u3010\u7761\u524D\u7701\u6D41\u7B2C\u4E00\u671F\u3011\u7701\u6D41\u603B\u7ED3\u4EE5\u53CA\u6211\u7684\u770B\u6CD5" tabindex="-1">\u3010\u7761\u524D\u7701\u6D41\u7B2C\u4E00\u671F\u3011\u7701\u6D41\u603B\u7ED3\u4EE5\u53CA\u6211\u7684\u770B\u6CD5 <a class="header-anchor" href="#\u3010\u7761\u524D\u7701\u6D41\u7B2C\u4E00\u671F\u3011\u7701\u6D41\u603B\u7ED3\u4EE5\u53CA\u6211\u7684\u770B\u6CD5" aria-hidden="true">#</a></h2><div class="info custom-block"><p class="custom-block-title">\u4F5C\u8005\u4FE1\u606F</p><ul><li>ID\uFF1A \u8FDB\u6B65\u601D\u6F6E</li><li>\u5E73\u53F0\uFF1A\u77E5\u4E4E/bilibili</li><li>\u8054\u7CFB\u65B9\u5F0F\uFF1A1592985163@qq.com</li><li><a href="https://www.bilibili.com/video/BV15S4y1E7Ju" target="_blank" rel="noreferrer">\u89C6\u9891\u5185\u5BB9</a></li></ul></div><p>\u8FD9\u671F\u89C6\u9891\u7761\u524D\u6D88\u606F\u8BB2\u7684\u662F\u751F\u80B2\u7387\u4E0B\u964D\u7684\u73B0\u72B6\uFF0C\u4F46\u5176\u5B9E\u73B0\u72B6\u6211\u89C9\u5F97\u9996\u5148\u8981\u4EBA\u5927\u90E8\u5206\u610F\u8BC6\u5230\u4E3A\u4EC0\u4E48\u9700\u8981\u751F\u80B2\u7387\u4E0A\u5347\u3002</p><p>\u9A6C\u524D\u5352\u8BF4\u63D0\u95EE\u9898\u8981\u6709\u81EA\u5DF1\u7684\u89C2\u70B9\uFF0C\u4E91\u66E6\u66FF\u8EAB\u8868\u793A\uFF0C\u5BF9\u4E8E\u751F\u5B69\u5B50\u6211\u4EEC\u8981\u4ECE\u4E24\u4E2A\u89D2\u5EA6\u53BB\u9F13\u52B1\uFF0C\u5E76\u53BB\u51CF\u5C11\u4E24\u4E2A\u89D2\u5EA6\u7684\u95EE\u9898\uFF1A</p><p>\u7B2C\u4E00\uFF1A\u5B69\u5B50\u4EE3\u8868\u7740\u751F\u6D3B\u7684\u4E00\u79CD\u76EE\u7684\u548C\u671F\u671B\uFF0C\u4EBA\u7684\u751F\u6D3B\u6709\u4E24\u79CD\u53EF\u80FD\uFF0C\u4E3A\u4E86\u67D0\u79CD\u76EE\u7684\u800C\u751F\u6D3B\u548C\u4E3A\u4E86\u751F\u6D3B\u672C\u8EAB\u800C\u6D3B\u3002\u591A\u6570\u4EBA\u5F80\u5F80\u662F\u4E24\u8005\u7686\u6709\uFF0C\u4F46\u73B0\u4EE3\u4E30\u5BCC\u7684\u5A31\u4E50\u63D0\u4F9B\u4E86\u4E00\u79CD\u5BF9\u4E8E\u540E\u8005\u66F4\u8BE6\u7EC6\u7684\u7EC6\u5206\uFF0C\u4EAB\u4E50\u4E3B\u4E49\u5F7B\u5F7B\u5E95\u5E95\u7684\u4E3A\u4E86\u611F\u5B98\u4EAB\u53D7\u751F\u6D3B\u3002</p><p>\u8FD9\u91CC\u4E0D\u6279\u5224\u4EFB\u4F55\u751F\u6D3B\u65B9\u5F0F\uFF0C\u53EA\u6307\u51FA\u4E00\u70B9\uFF0C\u540E\u73B0\u4EE3\u793E\u4F1A\u7684\u865A\u65E0\u4E3B\u4E49\u672C\u8EAB\u5C31\u662F\u793E\u4F1A\u6574\u4F53\u7F3A\u4E4F\u76EE\u6807\u624D\u4EA7\u751F\u7684\uFF0C\u4ECE\u5DE5\u4E1A\u53D1\u5C55\u65F6\u4EE3\u548C\u540E\u73B0\u4EE3\u7684\u5BF9\u6BD4\u6765\u770B\uFF0C\u6709\u76EE\u7684\u7684\u751F\u6D3B\u786E\u5B9E\u662F\u6BD4\u65E0\u76EE\u7684\u7684\u4EAB\u4E50\u7CBE\u795E\u66F4\u597D\u4E5F\u66F4\u6709\u66F4\u591A\u53EF\u80FD\u6027\u3002\u800C\u5728\u751F\u6D3B\u7684\u76EE\u7684\u4E2D\uFF0C\u7E41\u884D\u662F\u7EFC\u5408\u6027\u4EF7\u6BD4\u6700\u9AD8\u7684\uFF0C\uFF08\u6700\u5927\u7684\u53EF\u80FD\u6027\uFF0C\u548C\u8F83\u4F4E\u7684\u96BE\u5EA6\uFF0C\u548C\u5176\u4ED6\u8BF8\u5982\u521B\u4E1A\u7B49\u793E\u4F1A\u8FFD\u6C42\u6BD4\uFF09</p><p>\u7B2C\u4E8C\uFF1A\u4EBA\u53E3\u7ED3\u6784\u95EE\u9898\u4E0D\u4EC5\u4EC5\u662F\u89E3\u51B3\u517B\u8001\uFF0C\u66F4\u91CD\u8981\u7684\u662F\u53D1\u5C55\u53EF\u80FD\u6027\u3002\u66F4\u591A\u7684\u5E74\u8F7B\u4EBA\u53E3\u624D\u6709\u66F4\u591A\u7684\u53EF\u80FD\u6027\uFF0C\u5E74\u8F7B\u4EBA\u7684\u8BDE\u751F\u4E0D\u4EC5\u4EC5\u662F\u89E3\u51B3\u793E\u4F1A\u7684\u751F\u4EA7\u95EE\u9898\uFF0C\u800C\u662F\u80FD\u5E26\u6765\u66F4\u591A\u7684\u53EF\u80FD\u6027\uFF0C\u6BD4\u5982\u6211\u4EEC\u8FFD\u6C42\u5171\u4EA7\u4E3B\u4E49\u7684\u793E\u4F1A\uFF0C\u8BD5\u60F3\u4E00\u4E0B\u5982\u679C\u672A\u6765\u793E\u4F1A\u90FD\u662F\u8001\u5E74\u4EBA\u5360\u636E\u591A\u6570\uFF0C\u5E74\u8F7B\u4EBA\u5C11\u4E4B\u53C8\u5C11\uFF0C\u5171\u4EA7\u4E3B\u4E49\u7684\u793E\u4F1A\u6050\u6015\u5F88\u96BE\u6709\u51FA\u73B0\u7684\u53EF\u80FD\u6027\u3002</p><p>\u793E\u4F1A\u4F1A\u66F4\u503E\u5411\u4E8E\u8FFD\u6C42\u7A33\u5B9A\uFF0C\u5E74\u8F7B\u4EBA\u4E5F\u4F1A\u4E60\u60EF\u7528\u90A3\u4E9B\u71AC\u5E74\u9F84\u4F4E\u53D8\u5316\u7684\u751F\u6D3B\u5DE5\u4F5C\uFF0C\u793E\u4F1A\u8FDB\u6B65\u7684\u53EF\u80FD\u6027\u5C31\u4F1A\u66F4\u5C11\u3002</p><p>\u800C\u5E74\u8F7B\u4EBA\u66F4\u591A\uFF0C\u9996\u5148\u6709\u66F4\u591A\u4EBA\u613F\u610F\u53BB\u5C1D\u8BD5\uFF0C\u5176\u6B21\u66F4\u5927\u7684\u5C1D\u8BD5\u89C4\u6A21\u4F1A\u4EA7\u751F\u7C7B\u4F3C\u4E8E\u81EA\u7531\u5E02\u573A\u4E00\u6837\u7684\u673A\u5236\uFF0C\u793E\u4F1A\u7684\u53D8\u5316\u4F1A\u66F4\u6709\u53EF\u80FD\u5411\u597D\u7684\u89D2\u5EA6\u51FA\u53D1\u3002</p><h2 id="\u5E94\u8BD5\u4E4B\u6211\u89C1" tabindex="-1">\u5E94\u8BD5\u4E4B\u6211\u89C1 <a class="header-anchor" href="#\u5E94\u8BD5\u4E4B\u6211\u89C1" aria-hidden="true">#</a></h2><div class="info custom-block"><p class="custom-block-title">\u4F5C\u8005\u4FE1\u606F</p><ul><li>ID\uFF1A \u5341\u6708</li></ul></div><p>\u8FD9\u671F\uFF08\u300A460\u671F\u300B\uFF09\u89C6\u9891\u7684\u4E3B\u9898\u662F\u8003\u8BD5\u5236\u5EA6\uFF0C\u4EE5\u4E0B\u5185\u5BB9\u662F\u6211\u7684\u4E00\u4E9B\u8BFB\u540E\u5C0F\u601D\u8003\u4EE5\u53CA\u4E00\u4E9B\u89C6\u9891\u4E2D\u53EF\u80FD\u6CA1\u6709\u63D0\u53CA\u5230\u7684\u6211\u7684\u60F3\u6CD5\uFF1A</p><p>\u9996\u5148\uFF0C\u4EE5\u4E0B\u7ED3\u8BBA\u7684\u4F9D\u636E\u662F\u201C\u6211\u56FD\u793E\u4F1A\u4E3B\u8981\u77DB\u76FE\u662F\u4EBA\u6C11\u65E5\u76CA\u589E\u957F\u7684\u7F8E\u597D\u751F\u6D3B\u9700\u8981\u548C\u4E0D\u5E73\u8861\u4E0D\u5145\u5206\u7684\u53D1\u5C55\u4E4B\u95F4\u7684\u77DB\u76FE\u201D\u3002\u65E0\u8BBA\u662F\u9AD8\u8003\u4E5F\u597D\uFF0C\u8FD8\u662F\u8003\u7F16\u4E5F\u597D\uFF0C&quot;\u5C0F\u9547\u505A\u9898\u5BB6&quot;\u4EEC\u7684\u672C\u8D28\u9700\u6C42\u662F\u5C06\u9009\u62E9\u597D\u5C97\u4F4D\u7684\u201C\u7279\u6743\u201D\u5360\u4E3A\u5DF1\u6709\u3002</p><p>\u73B0\u9636\u6BB5\uFF0C\u53EA\u8981\u8D44\u6E90\u6709\u9650\uFF0C\u8003\u8BD5\u5C31\u65E0\u6CD5\u6446\u8131\u591A\u6570\u4EBA\u4E89\u5C11\u6570\u7279\u6743\uFF08\u83B7\u5F97\u9009\u62E9\u597D\u5B66\u6821\u7684\u7279\u6743\u3001\u83B7\u53D6\u201C\u94C1\u996D\u7897\u201D\u7F16\u5236\uFF09\u73B0\u72B6\uFF0C\u8861\u6C34\u4E2D\u5B66\u7684\u6F14\u8BB2\u53E3\u53E3\u58F0\u58F0\u8BF4\u81EA\u5DF1\u662F\u4E3A\u4E86\u6539\u53D8\u547D\u8FD0\uFF0C\u4F46\u662F\u4ED6\u53EA\u80FD\u6539\u53D8\u4ED6\u81EA\u5DF1\u7684\u547D\u8FD0\uFF0C\u800C\u4E14\u6BCF\u6709\u4E00\u4E2A\u8861\u6C34\u4E2D\u5B66\u7684\u5B66\u751F\u6539\u53D8\u547D\u8FD0\uFF0C\u90A3\u4E48\u5C31\u4F1A\u6709\u4E00\u4E2A\u4EBA\u5931\u53BB\u6539\u53D8\u547D\u8FD0\u7684\u673A\u4F1A\u3002\u4E3A\u4EC0\u4E48\u4ED6\u7684\u6F14\u8BB2\u8FD9\u4E48\u4EE4\u4EBA\u53CD\u611F\uFF0C\u56E0\u4E3A\u4ED6\u60F3\u8003\u9AD8\u5206\u7684\u76EE\u7684\u662F\u53BB\u201C\u62F1\u57CE\u5E02\u91CC\u7684\u767D\u83DC\u201D\uFF0C\u662F\u4E3A\u4E86\u4ED6\u81EA\u5DF1\u7684\u9636\u7EA7\u63D0\u5347\uFF08\u751A\u81F3\u53EF\u80FD\u4ED6\u672C\u8EAB\u51FA\u8EAB\u5C31\u5E76\u4E0D\u5DEE\uFF09\uFF0C\u8DDF\u90A3\u4E9B\u201C\u4E00\u773C\u770B\u5230\u5934\u7684\u666E\u901A\u4EBA\u201D\u5212\u6E05\u754C\u9650\uFF0C\u800C\u4E0D\u662F\u901A\u8FC7\u63D0\u9AD8\u793E\u4F1A\u751F\u4EA7\u529B\uFF0C\u8BA9\u6BCF\u4E2A\u8003\u751F\u90FD\u83B7\u5F97\u6539\u53D8\u547D\u8FD0\u7684\u673A\u4F1A\u3002</p><p>\u2026\u2026\u6613\u8FD9\u6B21\u88AB\u5F55\u7528\u7684\u662F\u56FD\u5BB6\u8BDD\u5267\u9662\u7684\u6F14\u5458\u5C97\u4F4D\uFF0C\u56FD\u5BB6\u8BDD\u5267\u9662\u662F\u81EA\u6536\u81EA\u652F\u4E8B\u4E1A\u5355\u4F4D\uFF0C\u6536\u5165\u6765\u6E90\u4E0D\u662F\u8D22\u653F\u62E8\u6B3E\uFF0C\u90FD\u5F15\u53D1\u5982\u6B64\u5927\u4E0D\u6EE1\uFF0C\u7F16\u5236\u4E9F\u9700\u6539\u9769\u3002\u53C2\u8003\u73B0\u5728\u5927\u5B66\u7684\u975E\u5347\u5373\u8D70\u5236\uFF1A\u5728\u5F55\u7528\u671F\u5185\u5982\u679C\u6CA1\u6709\u8FBE\u6210\u4E00\u5B9A\u7684\u8003\u6838\u8981\u6C42\uFF0C\u90A3\u4E48\u540E\u7EED\u4E0D\u518D\u5F55\u7528\u6216\u8005\u964D\u7EA7\u3002\u7F16\u5236\u5185\u4E5F\u9700\u8981\u6307\u5B9A\u4E00\u5B9A\u7684\u8003\u6838\u8981\u6C42\uFF0C\u81EA\u6536\u81EA\u652F\u7684\u5355\u4F4D\u7531\u5355\u4F4D\u81EA\u884C\u6307\u5B9A\uFF0C\u8D22\u653F\u62E8\u6B3E\u7684\u7F16\u5236\u8003\u6838\u76EE\u6807\u9700\u8981\u63A5\u53D7\u7EB3\u7A0E\u4EBA\u7684\u76D1\u7763\u3002</p>',26),c=[s];function o(d,r,p,n,h,u){return e(),i("div",null,c)}const b=l(t,[["render",o]]);export{v as __pageData,b as default};
