import{_ as e,c as i,o as t,a}from"./app.bcd3b7af.js";const m=JSON.parse('{"title":"\u53E6\u7C7B\u70B9\u8BC4","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u672C\u4EBA\uFF08\u975E\u4E2D\u533B\u4E13\u4E1A\uFF09\u5BF9\u4E2D\u533B\u53CA\u4E2D\u534E\u6587\u660E\u7684\u4E00\u4E9B\u770B\u6CD5","slug":"\u672C\u4EBA\uFF08\u975E\u4E2D\u533B\u4E13\u4E1A\uFF09\u5BF9\u4E2D\u533B\u53CA\u4E2D\u534E\u6587\u660E\u7684\u4E00\u4E9B\u770B\u6CD5","link":"#\u672C\u4EBA\uFF08\u975E\u4E2D\u533B\u4E13\u4E1A\uFF09\u5BF9\u4E2D\u533B\u53CA\u4E2D\u534E\u6587\u660E\u7684\u4E00\u4E9B\u770B\u6CD5","children":[]}],"relativePath":"5/inverse.md"}'),p={name:"5/inverse.md"},l=a('<h1 id="\u53E6\u7C7B\u70B9\u8BC4" tabindex="-1">\u53E6\u7C7B\u70B9\u8BC4 <a class="header-anchor" href="#\u53E6\u7C7B\u70B9\u8BC4" aria-hidden="true">#</a></h1><div class="danger custom-block"><p class="custom-block-title">\u9A6C\u7763\u516C\u70B9\u8BC4</p><p>\u4EE3\u8868\u4E86\u4E00\u79CD\u5178\u578B\u601D\u6F6E\uFF0C\u5BF9\u672C\u6587\u5185\u5BB9\u4E0D\u8868\u6001\uFF0C\u6743\u505A\u5404\u4F4D\u8BFB\u8005\u53C2\u8003\u3002</p></div><h2 id="\u672C\u4EBA\uFF08\u975E\u4E2D\u533B\u4E13\u4E1A\uFF09\u5BF9\u4E2D\u533B\u53CA\u4E2D\u534E\u6587\u660E\u7684\u4E00\u4E9B\u770B\u6CD5" tabindex="-1">\u672C\u4EBA\uFF08\u975E\u4E2D\u533B\u4E13\u4E1A\uFF09\u5BF9\u4E2D\u533B\u53CA\u4E2D\u534E\u6587\u660E\u7684\u4E00\u4E9B\u770B\u6CD5 <a class="header-anchor" href="#\u672C\u4EBA\uFF08\u975E\u4E2D\u533B\u4E13\u4E1A\uFF09\u5BF9\u4E2D\u533B\u53CA\u4E2D\u534E\u6587\u660E\u7684\u4E00\u4E9B\u770B\u6CD5" aria-hidden="true">#</a></h2><div class="info custom-block"><p class="custom-block-title">\u4F5C\u8005\u4FE1\u606F</p><ul><li>ID\uFF1A \u767E\u8349\u971C</li><li>\u5E73\u53F0\uFF1AB \u7AD9</li></ul></div><p>\u4E0D\u77E5\u9053\u6BCF\u4E2A\u4EBA\u5BF9\u672A\u6765\u533B\u5B66\u7684\u770B\u6CD5\u662F\u600E\u6837\u7684\uFF0C\u4F46\u662F\u6211\u89C9\u5F97\u4E2D\u533B\u5C31\u662F\u672A\u6765\u533B\u5B66\u3002\u5373\u4E00\u4E2A\u533B\u751F\u4EE5\u4EBA\u7684\u957F\u5BFF\u4E3A\u524D\u63D0\u5BF9\u75C5\u4EBA\u8FDB\u884C\u6240\u6709\u65B9\u5411\u7684\u6CBB\u7597\u3002\u4E5F\u8BB8\u533B\u751F\u5C06\u6765\u53EF\u4EE5\u7531\u673A\u5668\u53D6\u4EE3\u3002</p><p>\u5C31\u6211\u76EE\u524D\u7684\u89C2\u5BDF\uFF0C\u533B\u9662\u53EA\u662F\u4E00\u4E2A\u6CBB\u75C5\u7684\u5730\u65B9\uFF0C\u8FD9\u662F\u5B83\u7684\u4F18\u52BF\uFF0C\u5728\u8EAB\u4F53\u7684\u5176\u4ED6\u65B9\u9762\u90FD\u5065\u5EB7\u7684\u524D\u63D0\u4E0B\uFF0C\u6CBB\u7597\u597D\u5355\u4E2A\u75BE\u75C5\u800C\u4E14\u4E0D\u7528\u627F\u62C5\u5176\u4ED6\u8D23\u4EFB\uFF0C\u8FD9\u89E3\u51B3\u4E86\u7EDD\u5927\u90E8\u5206\u7684\u95EE\u9898\uFF0C\u66F4\u50CF\u662F\u5DE5\u5382\u7684\u6D41\u6C34\u7EBF\u3002\u5BF9\u75C5\u4EBA\u6765\u8BF4\u7684\u95EE\u9898\u6709\uFF1A\u4E00\u3001\u4EE5\u836F\u7269\u4E3A\u524D\u63D0\u7684\u6CBB\u7597\u4F1A\u5BFC\u81F4\u533B\u836F\u96C6\u56E2\u63A7\u5236\u533B\u9662\uFF08\u5305\u542B\u5BF9\u56FD\u5BB6\u7684\u5F71\u54CD\uFF09\uFF1B\u4E8C\u3001\u533B\u751F\u63A7\u5236\u4E0D\u4E86\u836F\u7269\u7684\u526F\u4F5C\u7528\uFF0C\u75C5\u4EBA\u5F80\u5F80\u8981\u9762\u4E34\u5403\u53E6\u4E00\u79CD\u836F\u6765\u5BF9\u6297\u524D\u4E00\u79CD\u836F\u7684\u526F\u4F5C\u7528\uFF1B\u4E09\u3001\u9700\u8981\u6CBB\u7597\u7684\u5355\u4E2A\u75BE\u75C5\u5F80\u5F80\u4E0D\u662F\u673A\u68B0\u7684\u51FA\u73B0\uFF0C\u5B83\u662F\u4EBA\u4F53\u7684\u590D\u6742\u8FD0\u884C\u8FC7\u7A0B\u51FA\u73B0\u7684\u4EA7\u7269\uFF0C\u6CBB\u7597\u4E00\u79CD\u75BE\u75C5\u53EF\u80FD\u53CD\u800C\u9020\u6210\u53E6\u4E00\u79CD\u540E\u679C\u3002\u53E6\u5916\uFF0C\u5236\u836F\u7684\u6709\u51E0\u4E2A\u4EBA\u662F\u4E00\u7EBF\u533B\u751F\uFF1F</p><p>\u4E2D\u533B\u7684\u6839\u672C\u662F\u201C\u4EBA\u8EAB\u867D\u5C0F\u6697\u5408\u5929\u5730\u201D\uFF0C\u4EE5\u4E2D\u56FD\u6700\u6839\u672C\u7684\u7ED3\u679C\u8BBA\u4E3A\u6CBB\u7597\u65B9\u5411\u3002\u76EE\u7684\u662F\u57F9\u517B\u66F4\u63A5\u8FD1\u81EA\u7136\u7684\u9AD8\u7EA7\u4EBA\u7C7B\u3002\u5B83\u662F\u4E00\u95E8\u4EE5\u4EBA\u4E3A\u672C\u7684\u590D\u6742\u5B66\u79D1\uFF0C\u800C\u4EBA\u7C7B\u672C\u5E94\u8FDB\u884C\u590D\u6742\u5DE5\u4F5C\uFF0C\u8FD9\u662F\u4EBA\u7C7B\u7684\u8FDB\u5316\u8FC7\u7A0B\u51B3\u5B9A\u7684\u3002\u73B0\u4EE3\u4E2D\u533B\u7684\u6700\u5927\u8D25\u7B14\u5C31\u662F\u4E2D\u533B\u9662\u7684\u5206\u79D1\u6CBB\u7597\u3001\u533B\u836F\u5206\u79BB\u3001\u9488\u836F\u5206\u79BB\u3002\u4F24\u5BD2\u4E2D\u7684\u7ECF\u65B9\uFF0C\u5C31\u50CF\u662F\u7B54\u6848\u4E00\u6837\u597D\u7528\uFF0C\u8FD9\u662F\u53E4\u4EBA\u7559\u4E0B\u7684\u5386\u53F2\u9057\u4EA7\uFF0C\u6211\u4EEC\u53EF\u4EE5\u968F\u610F\u4F7F\u7528\uFF0C\u53EA\u662F\u8FD9\u7EC8\u7A76\u4E0D\u662F\u4E0A\u4E58\uFF0C\u4F18\u79C0\u7684\u533B\u751F\u9700\u8981\u5B66\u4F1A\u7ECF\u65B9\u7684\u9053\u7406\uFF0C\u5E76\u4E14\u6D3B\u5B66\u6D3B\u7528\uFF0C\u4E0D\u62D8\u6CE5\u4E8E\u67D0\u4E00\u5473\u836F\u3002\u4EBA\u4F53\u548C\u5927\u81EA\u7136\u90FD\u662F\u590D\u6742\u7CFB\u7EDF\uFF0C\u901A\u8FC7\u8BAD\u7EC3\u7684\u4E2D\u533B\u53EF\u4EE5\u5C06\u4E24\u4E2A\u7CFB\u7EDF\u8FDB\u884C\u5408\u7406\u9002\u914D\uFF0C\u81EA\u7136\u7684\u7CFB\u7EDF\u9AD8\u4E8E\u4EBA\u7C7B\u7684\u7CFB\u7EDF\uFF0C\u4E2D\u533B\u53EF\u4EE5\u501F\u7528\u81EA\u7136\u4ECE\u5404\u79CD\u89D2\u5EA6\u8FDB\u884C\u540C\u4E00\u79CD\u75C7\u72B6\u7684\u6CBB\u7597\uFF0C\u8FDB\u884C\u9488\u5BF9\u6027\u6CBB\u7597\uFF0C\u666E\u901A\u7684\u533B\u751F\u662F\u624B\u5DE5\u4E1A\u8005\uFF0C\u4F18\u79C0\u7684\u533B\u751F\u66F4\u50CF\u5927\u5E08\uFF1B\u73B0\u4EE3\u533B\u5B66\u662F\u4EBA\u7C7B\u521B\u9020\u7684\u7CFB\u7EDF\uFF0C\u5B83\u5C0F\u4E8E\u4EBA\u4F53\u7684\u590D\u6742\u7CFB\u7EDF\uFF0C\u6240\u4EE5\u53EF\u4EE5\u66F4\u597D\u3001\u66F4\u89C4\u8303\u7684\u8BAD\u7EC3\u533B\u751F\uFF0C\u4EC5\u4F7F\u7528\u89C4\u5B9A\u7684\u836F\u7269\u8FDB\u884C\u6CBB\u7597\uFF0C\u6240\u4EE5\u66F4\u50CF\u5DE5\u5382\u548C\u5DE5\u4EBA\u3002\u7763\u5DE5\u8BF4\u8981\u5E9F\u533B\u9A8C\u836F\uFF0C\u8FD9\u5C31\u50CF\u4EE5\u524D\u7684\u6253\u5012\u5B54\u5BB6\u5E97\uFF0C\u771F\u5B9E\u76EE\u7684\u662F\u6551\u51FA\u5B54\u5B50\uFF0C\u5728\u8FD9\u91CC\u662F\u5B58\u836F\u5931\u533B\uFF0C\u5219\u533B\u836F\u4E24\u5931\uFF0C\u5931\u53BB\u4E2D\u533B\u7684\u4E2D\u836F\u4E0D\u662F\u4E2D\u836F\uFF0C\u53EA\u662F\u5404\u79CD\u81EA\u7136\u754C\u5B58\u5728\u7684\u7269\u4F53\u3002</p><p>\u4E2D\u533B\u7684\u57FA\u7840\u662F\u9EC4\u5E1D\u5185\u7ECF\uFF0C\u4F24\u5BD2\u6742\u75C5\u8BBA\u3002\u8FD9\u4E24\u672C\u4E66\u7684\u5B58\u5728\u76F8\u5F53\u4E8E\u9A6C\u514B\u601D\u4E3B\u4E49\u5BF9\u4E2D\u56FD\u7684\u5F71\u54CD\u3002\u5386\u53F2\u8BC1\u660E\u4E86\u8FD9\u4E24\u672C\u4E66\u7684\u5408\u7406\u6027\uFF0C\u5C31\u50CF\u5386\u53F2\u540C\u6837\u8BC1\u660E\u4E86\u9A6C\u514B\u601D\u4E3B\u4E49\u7684\u5408\u7406\u6027\u3002</p><p>\u6709\u4EBA\u8BF4\uFF0C\u4E2D\u533B\u7684\u54F2\u5B66\u57FA\u7840\u9634\u9633\u4E94\u884C\u5C5E\u4E8E\u5F62\u800C\u4E0A\u5B66\u7684\u4E1C\u897F\uFF0C\u6211\u633A\u597D\u5947\uFF0C\u8FD9\u4E9B\u4EBA\u662F\u5426\u770B\u8FC7\u9EC4\u5E1D\u5185\u7ECF\uFF0C\u5C31\u50CF\u6211\u4E5F\u4E0D\u77E5\u9053\u8FD9\u4E9B\u4EBA\u662F\u5426\u4E86\u89E3\u8FC7\u9A6C\u514B\u601D\u4E3B\u4E49\u3001\u6BDB\u6CFD\u4E1C\u601D\u60F3\u3001\u9093\u5C0F\u5E73\u7406\u8BBA\uFF0C\u4F46\u8FD9\u4E5F\u540C\u6837\u5F71\u54CD\u4E0D\u4E86\u8FD9\u4E9B\u4EBA\u751F\u6D3B\u5728\u4E2D\u56FD\u5E76\u4E14\u5EFA\u8BBE\u4E2D\u56FD\u3002\u9634\u9633\u4E94\u884C\u53EA\u662F\u54F2\u5B66\u5DE5\u5177\uFF0C\u662F\u53E4\u4EBA\u8BA4\u8BC6\u4E16\u754C\u4EA7\u751F\u7684\u7ED3\u679C\uFF0C\u662F\u4E00\u4E2A\u540D\u79F0\uFF0C\u8FD9\u53D7\u9650\u4E8E\u793E\u4F1A\u7684\u53D1\u5C55\u6C34\u5E73\u3002\u73B0\u4EE3\u4E2D\u56FD\u4EBA\u7684\u601D\u7EF4\u65B9\u5F0F\u5F88\u5927\u7A0B\u5EA6\u4E0A\u53D7\u897F\u65B9\u4EBA\u7684\u5F71\u54CD\uFF0C\u4E2D\u534E\u6587\u660E\u5386\u53F2\u4E0A\u4E5F\u7ECF\u5386\u8FC7\u591A\u91CD\u78E8\u96BE\uFF0C\u517C\u6536\u5E76\u84C4\u800C\u6700\u540E\u5C79\u7ACB\u4E0D\u5012\u7684\u4E5F\u53EA\u6709\u4E2D\u534E\u6C11\u65CF\uFF0C\u4F46\u662F\u5982\u679C\u629B\u5F03\u4E2D\u534E\u6C11\u65CF\u7684\u6839\u57FA\uFF0D\uFF0D\u9690\u85CF\u4E8E\u4E07\u7269\u7684\u54F2\u5B66\u601D\u7EF4\uFF0C\u6050\u6015\u79BB\u706D\u4EA1\u4E5F\u4E0D\u8FDC\u4E86\u3002</p><p>\u4E5F\u6709\u4EBA\u8BF4\uFF0C\u4E2D\u533B\u91CC\u7684\u9B42\u3001\u9B44\u3001\u6C14\uFF0C\u8FD9\u4E09\u6837\u4E1C\u897F\u5C31\u662F\u5178\u578B\u7684\u552F\u5FC3\u4E3B\u4E49\uFF0C\u56E0\u4E3A\u6839\u672C\u770B\u4E0D\u89C1\uFF0C\u65E0\u6CD5\u8BC1\u660E\u5B83\u7684\u5B58\u5728\u3002\u90A3\u6211\u5C31\u8981\u63D0\u95EE\u4E86\uFF0C\u6697\u7269\u8D28\u662F\u4EC0\u4E48\uFF1F\u6570\u5343\u5E74\u65F6\u95F4\u5DEE\u7684\u51E0\u4E2A\u540D\u79F0\u653E\u5728\u4EBA\u7C7B\u7684\u8EAB\u4E0A\u663E\u5F97\u6F2B\u957F\uFF0C\u653E\u5728\u5B87\u5B99\u7EAC\u5EA6\u6765\u770B\u5462\uFF1F\u4E00\u4E2A\u662F\u901A\u8FC7\u6570\u5343\u5E74\u7684\u89C2\u5BDF\u4EBA\u4F53\u53D1\u73B0\u7684\u6A21\u7CCA\u6982\u5FF5\uFF0C\u4E00\u4E2A\u662F\u901A\u8FC7\u89C2\u5BDF\u5B87\u5B99\u540E\u53D1\u73B0\u7684\u6A21\u7CCA\u6982\u5FF5\uFF0C\u4E24\u8005\u76F8\u5DEE\u7684\u4EC5\u662F\u4E00\u4E2A\u6709\u5145\u8DB3\u7684\u5B9E\u9A8C\u8BC1\u660E\uFF0C\u800C\u53E6\u4E00\u4E2A\uFF0C\u56E0\u4E3A\u5E74\u4EE3\u4E45\u8FDC\uFF0C\u5B9E\u9A8C\u6570\u636E\u4E22\u5931\u3002\u5E76\u4E14\u6211\u60F3\u95EE\u4E00\u4E2A\u95EE\u9898\uFF0C\u79D1\u5B66\u89C4\u5F8B\u662F\u521B\u9020\u51FA\u6765\u7684\u5417\uFF1F</p><p>\u4E2D\u56FD\u7684\u73B0\u4EE3\u53D1\u5C55\u5F88\u5927\u7A0B\u5EA6\u4E0A\u53D7\u5230\u4E86\u7ACB\u56FD\u4E4B\u672C\uFF0D\uFF0D\u552F\u7269\u4E3B\u4E49\u7684\u9650\u5236\u3002\u53D7\u5230\u4E86\u552F\u7269\u4E3B\u4E49\u6559\u80B2\u4E3A\u57FA\u7840\u7684\u79D1\u5B66\u6559\u80B2\u7684\u4E2D\u56FD\u4EBA\u5929\u7136\u5BF9\u4E2D\u534E\u6587\u5316\u7684\u552F\u5FC3\u4E3B\u4E49\u656C\u800C\u8FDC\u4E4B\u5E76\u4E14\u5145\u6EE1\u4E86\u50B2\u6162\u548C\u504F\u89C1\u3002\u4E2D\u534E\u6587\u5316\u7684\u7CBE\u534E\u76F8\u5F53\u7A0B\u5EA6\u4E0A\u662F\u73B0\u4EE3\u4EBA\u7406\u89E3\u7684\u552F\u5FC3\u4E3B\u4E49\uFF0C\u8FD9\u4E9B\u4E1C\u897F\u662F\u53E4\u4EBA\u7684\u5047\u8BBE\u3001\u63A8\u7406\u4E0E\u9A8C\u8BC1\u800C\u53D7\u5236\u4E8E\u65F6\u4EE3\u53D1\u5C55\uFF0C\u4F46\u8FD9\u4E2A\u4F53\u7CFB\u56DE\u7B54\u4E86\u6211\u662F\u8C01\uFF0C\u6211\u4ECE\u54EA\u91CC\u6765\uFF0C\u6211\u8981\u5230\u54EA\u91CC\u53BB\u7684\u95EE\u9898\uFF1A\u6211\u8BA4\u4E3A\u4E2D\u534E\u6587\u660E\u5DF2\u7ECF\u89E3\u51B3\u4E86\u8FD9\u4E2A\u7684\u95EE\u9898\u3002\u5B83\u6709\u4E00\u4E2A\u5B97\u6559\u7684\u5916\u58F3\uFF0C\u4F46\u662F\u548C\u897F\u65B9\u5B97\u6559\u7684\u672C\u8D28\u5E76\u4E0D\u76F8\u540C\uFF0C\u4EE5\u6211\u4EEC\u5728\u552F\u7269\u4E3B\u4E49\u7684\u89C6\u89D2\u4E0B\u73B0\u5728\u65E0\u6CD5\u7406\u89E3\u7684\u89D2\u5EA6\uFF0C\u5C31\u662F\u4ED6\u4EEC\u4F5C\u4E3A\u4E2A\u4F53\u6BCF\u4E2A\u4EBA\u90FD\u6210\u529F\u7684\u4EE5\u4E2A\u4F53\u8EAB\u4EFD\u56DE\u7B54\u4E86\u6211\u662F\u8C01\u6211\u4ECE\u90A3\u91CC\u6765\u6211\u8981\u5230\u54EA\u91CC\u53BB\u7684\u95EE\u9898\uFF0C\u6211\u8BA4\u4E3A\u4E2D\u533B\u662F\u8FD9\u4E2A\u56DE\u7B54\u7684\u5E73\u884C\u4EA7\u7269\u3002\u56E0\u6B64\u89E3\u51B3\u4E86\u8FD9\u4E09\u4E2A\u95EE\u9898\u540E\u4E2D\u534E\u6587\u660E\u6570\u5343\u5E74\u6CA1\u6709\u53D1\u5C55\u3002\u4E5F\u56E0\u6B64\u4EA7\u751F\u4E86\u72EC\u7279\u7684\u4E2D\u5F0F\u8003\u53E4\uFF08\u4F53\u73B0\u5728\u5C0F\u8BF4\u4E2D\uFF0C\u5C31\u662F\u4E3B\u89D2\u5F80\u5F80\u4ECE\u60AC\u5D16\u4E0B\u6361\u5230\u524D\u4EBA\u9057\u7559\u7684\u79D8\u7C4D\uFF0C\u6709\u8DA3\u7684\u662F\u79D8\u7C4D\u5C31\u5728\u8EAB\u8FB9\u4F8B\u5982\u897F\u6E38\u8BB0\uFF09\u3002\u7740\u6025\u5426\u5B9A\u5F88\u53EF\u80FD\u4F1A\u4E22\u6389\u6587\u660E\u7687\u51A0\u4E0A\u7684\u660E\u73E0\u800C\u88AB\u897F\u65B9\u4EBA\u6361\u8D77\uFF0C\u8FD9\u4E00\u70B9\u5BF9\u6211\u6765\u8BF4\u662F\u65E0\u6CD5\u63A5\u53D7\u7684\u3002</p><p>\u73B0\u4EE3\u4E2D\u56FD\u4EBA\u4ECE\u5C0F\u63A5\u53D7\u7684\u6559\u80B2\u57FA\u7840\u5C31\u662F\u897F\u65B9\u4EBA\u521B\u7ACB\u7684\u79D1\u5B66\u4F53\u7CFB\uFF0C\u5E76\u4E14\u4ECE\u9A6C\u514B\u601D\u4E3B\u4E49\u5230\u79D1\u5B66\u57FA\u7840\u5230\u5148\u8FDB\u7684\u79D1\u5B66\u6210\u679C\uFF0C\u6700\u597D\u7684\u4E1C\u897F\u5F80\u5F80\u5168\u662F\u897F\u65B9\u4EBA\uFF0C\u5E76\u4E14\u5B83\u4EEC\u65E2\u662F\u88C1\u5224\u4E5F\u662F\u9009\u624B\uFF0C\u4E2D\u56FD\u4EBA\u5F88\u96BE\u7A81\u7834\u8FD9\u4E2A\u9650\u5236\uFF0C\u4E5F\u56E0\u6B64\u51FA\u73B0\u4E86\u5F88\u591A\u7688\u4F9D\u8005\u72C2\u70ED\uFF0C\u5F80\u5F80\u4F1A\u51FA\u73B0\u5B66\u7684\u8D8A\u597D\u5C41\u80A1\u8D8A\u6B6A\uFF0C\u6211\u5E38\u5E38\u90FD\u5728\u60F3\u6559\u80B2\u662F\u4E0D\u662F\u5DF2\u7ECF\u5931\u8D25\u4E86\uFF0C\u4EE5\u81F3\u4E8E\u7EDD\u5927\u591A\u6570\u4EBA\u4E0D\u4EC5\u6CA1\u6709\u89E3\u653E\u5168\u4EBA\u7C7B\u7684\u4FE1\u5FF5\u548C\u52C7\u6C14\uFF0C\u53CD\u800C\u628A\u91D1\u94B1\u4F5C\u4E3A\u5B9E\u73B0\u81EA\u6211\u7684\u552F\u4E00\u9014\u5F84\u3002\u6211\u8BA4\u4E3A\u89E3\u6CD5\u53EA\u6709\u628A\u9A6C\u514B\u601D\u8865\u5145\u8FDB\u5165\u4E2D\u534E\u6587\u5316\u7684\u54F2\u5B66\u5B9D\u5E93\u3002</p><p>\u6211\u4E0D\u77E5\u9053\u662F\u5426\u6709\u4EBA\u505A\u8FC7\u53E4\u4EE3\u6CA1\u6709\u800C\u73B0\u4EE3\u4EBA\u72EC\u6709\u7684\u75C5\u7684\u8C03\u67E5\uFF0C\u5982\u679C\u505A\u8FC7\uFF0C\u6211\u5F88\u60F3\u77E5\u9053\uFF0C\u8FD9\u4E9B\u75BE\u75C5\u7684\u81F4\u75C5\u539F\u56E0\u548C\u73B0\u4EE3\u5DE5\u4E1A\u7684\u5173\u8054\u3002</p><p>\u4E2D\u533B\u7684\u8FDB\u6B65\u4E4B\u6CD5\u76EE\u524D\u6765\u8BB2\u6211\u8BA4\u4E3A\u5E94\u8BE5\u662F\u5F15\u5165\u6700\u4F18\u79C0\u7684\u7269\u7406\u5B66\u5BB6\u5E76\u4E14\u9488\u5BF9\u73B0\u4EE3\u5DE5\u4E1A\u521B\u7ACB\u65B0\u7684\u5B66\u8BF4\u3002\u6211\u4E5F\u8BBE\u60F3\u8FC7\u4E2D\u533B\u7684\u6D88\u5931\uFF0C\u76EE\u524D\u4E3A\u6B62\uFF0C\u4EBA\u7C7B\u8FD8\u811A\u8E29\u5927\u5730\uFF0C\u5934\u9876\u5929\u7A7A\uFF0C\u4E2D\u533B\u4E0D\u4F1A\u5931\u53BB\u751F\u547D\u529B\u3002\u4E5F\u8BB8\u5C31\u662F\u5F53\u4EBA\u7C7B\u8FDB\u5165\u592A\u7A7A\u7684\u90A3\u4E00\u523B\uFF0C\u4E2D\u533B\u624D\u5230\u4E86\u5BFF\u7EC8\u6B63\u5BDD\u7684\u65F6\u5019\uFF0C\u4F46\u662F\u5B83\u7559\u4E0B\u6765\u7684\u54F2\u5B66\u6C38\u8FDC\u5B58\u5728\uFF0C\u56E0\u800C\u5B83\u4E0D\u4F1A\u771F\u7684\u6B7B\u4EA1\uFF0C\u53EA\u4F1A\u6362\u4E00\u79CD\u6A21\u5F0F\u5B58\u5728\u4E8E\u5B87\u5B99\u4E2D\u3002</p><p>\u6211\u4EEC\u53EA\u6709\u5148\u4FDD\u4F4F\u4E2D\u534E\u6587\u660E\u8FD9\u4E2A\u57FA\u5730\uFF0C\u624D\u6709\u53EF\u80FD\u89E3\u653E\u5168\u4E16\u754C\u3002</p><p>\uFF08\u5982\u679C\u6709\u673A\u4F1A\u6211\u5E0C\u671B\u7763\u5DE5\u53BB\u4E00\u8D9F\u6B66\u5F53\uFF0C\u4E0E\u4E00\u4E9B\u8001\u9053\u957F\u6DF1\u5165\u4EA4\u6D41\uFF0C\u53D1\u8868\u4E00\u4E0B\u5BF9\u552F\u7269\u4E3B\u4E49\u4E0D\u76F8\u4FE1\u7684\u4E1C\u897F\u7684\u770B\u6CD5\uFF09</p><p>\u6211\u5728\u6B64\u7279\u610F\u63D0\u51FA\u51E0\u4E2A\u95EE\u9898</p><ol><li>\u540C\u6837\u662F\u624B\u5DE5\u4E1A\u8005\uFF0C\u51ED\u4EC0\u4E48\u8BA4\u4E3A\u88AB\u7269\u63A7\u5236\u4EBA\u7684\u73B0\u4EE3\u533B\u5B66\u8005\u7406\u5FF5\u4E0A\u9AD8\u4E8E\u4EBA\u63A7\u5236\u7269\u7684\u53E4\u4EE3\u533B\u5B66\u8005\u3002</li><li>\u79D1\u5B66\u89C4\u5F8B\u4E3A\u4EC0\u4E48\u662F\u53D1\u73B0\u800C\u4E0D\u662F\u53D1\u660E\u3002</li><li>\u4E3A\u4EC0\u4E48\u6211\u4EEC\u53D1\u5C55\u4E2D\u56FD\u7279\u8272\u793E\u4F1A\u4E3B\u4E49\u800C\u4E0D\u76F4\u63A5\u4F7F\u7528\u9A6C\u514B\u601D\u7684\u79D1\u5B66\u793E\u4F1A\u4E3B\u4E49\u6216\u82CF\u8054\u793E\u4F1A\u4E3B\u4E49\u3002</li><li>\u628A\u4E2D\u533B\u5B9A\u4E49\u4E3A\u9700\u8981\u6D88\u706D\u7684\u7384\u5B66\u548C\u628A\u9003\u8DD1\u4E3B\u4E49\u5B9A\u4E49\u4E3A\u975E\u6CD5\u6709\u533A\u522B\u5417\u3002</li></ol>',18),s=[l];function c(o,r,n,d,_,h){return t(),i("div",null,s)}const f=e(p,[["render",c]]);export{m as __pageData,f as default};
