import{_ as e,c as p,o as a,a as t}from"./app.7fc5d397.js";const f=JSON.parse('{"title":"\u4F53\u9A8C","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u4E2D\u533B\u9662\u7684\u4E00\u540D\u666E\u901A\u804C\u5DE5","slug":"\u4E2D\u533B\u9662\u7684\u4E00\u540D\u666E\u901A\u804C\u5DE5","link":"#\u4E2D\u533B\u9662\u7684\u4E00\u540D\u666E\u901A\u804C\u5DE5","children":[]}],"relativePath":"7/experiences.md"}'),s={name:"7/experiences.md"},c=t('<h1 id="\u4F53\u9A8C" tabindex="-1">\u4F53\u9A8C <a class="header-anchor" href="#\u4F53\u9A8C" aria-hidden="true">#</a></h1><h2 id="\u4E2D\u533B\u9662\u7684\u4E00\u540D\u666E\u901A\u804C\u5DE5" tabindex="-1">\u4E2D\u533B\u9662\u7684\u4E00\u540D\u666E\u901A\u804C\u5DE5 <a class="header-anchor" href="#\u4E2D\u533B\u9662\u7684\u4E00\u540D\u666E\u901A\u804C\u5DE5" aria-hidden="true">#</a></h2><div class="info custom-block"><p class="custom-block-title">\u4F5C\u8005\u4FE1\u606F</p><ul><li>ID\uFF1A\u602A\u529B\u5C0F\u795E</li></ul></div><p>\u7763\u5DE5\u597D\uFF0C\u4E00\u76F4\u4EE5\u6765\u90FD\u6709\u6536\u770B\u60A8\u7684\u89C6\u9891\uFF0C\u8C22\u8C22\u7763\u5DE5\u7ED9\u4E86\u6211\u66F4\u591A\u89C2\u5BDF\u8FD9\u4E2A\u4E16\u754C\u548C\u4E86\u89E3\u8FD9\u4E2A\u76F4\u63A5\u5230\u601D\u7EF4\u65B9\u5F0F\uFF0C\u66F4\u52A0\u7406\u6027\u548C\u5BA2\u89C2\u5730\u770B\u5230\u70ED\u95E8\u65B0\u95FB\u3002</p><p>\u6211\u662F**\u7701**\u5E02\u4E2D\u533B\u9662\u7684\u4E00\u540D\u666E\u901A\u804C\u5DE5\uFF0C\u4ECE\u5927\u5B66\u7684\u65F6\u5019\u63A5\u89E6\u5230\u5218\u5927\u53EF\u5C31\u5728\u5F00\u59CB\u53CD\u601D\u81EA\u5DF1\u6240\u5B66\u7684\u4E2D\u533B\u5B66\u5230\u5E95\u662F\u79D1\u5B66\u8FD8\u662F\u7384\u5B66\uFF0C\u4ECE\u5218\u5927\u53EF\u5F00\u59CB\u6162\u6162\u63A5\u89E6\u5230\u4E86\u7763\u5DE5\u3002</p><p>\u56E0\u4E3A\u751F\u8BA1\u95EE\u9898\uFF0C\u5373\u4F7F\u5BF9\u4E2D\u533B\u6709\u4E86\u66F4\u591A\u7684\u53CD\u601D\u548C\u601D\u8003\uFF0C\u4F46\u8FD8\u662F\u7559\u5728\u4E86\u4E2D\u533B\u9662\u7CCA\u53E3\u996D\u5403\u5403\uFF0C\u6BD5\u7ADF\u662F\u7F16\u5236\uFF0C\u63D0\u4F9B\u4E86\u751F\u5B58\u4E8E\u4E16\u7684\u4FDD\u969C\uFF0C\u6CA1\u6709\u52C7\u6C14\u8DF3\u51FA\u6765\u76F4\u6307\u4E2D\u533B\u3002</p><p>\u5165\u804C\u4E2D\u533B\u9662\u4EE5\u540E\uFF0C\u53BB\u7701\u91CC\u7684\u533B\u9662\u89C4\u57F9\u4E86\u4E09\u5E74\uFF0C\u5BF9\u4E8E\u6DF7\u65E5\u5B50\u7684\u4EBA\u6765\u8BF4\uFF0C\u5012\u4E5F\u5F00\u5FC3\uFF1B\u56DE\u5230\u81EA\u5DF1\u533B\u9662\u7684\u8FD9\u4E24\u5E74\uFF0C\u8D8A\u6765\u8D8A\u611F\u53D7\u5230\u4E86\u4E0E\u4E2D\u533B\u7684\u5BF9\u6297\uFF0C\u5185\u5FC3\u82E6\u4E0D\u582A\u8A00\u3002</p><p>\u521A\u56DE\u5230\u81EA\u5DF1\u533B\u9662\uFF0C\u7B2C\u4E00\u4E2A\u6708\u9664\u5F00\u57FA\u672C\u5DE5\u8D44\uFF0C\u53EA\u6709 30 \u5143\u7684\u5956\u91D1\uFF0C\u6DF1\u611F\u5DE5\u4F5C\u4E0D\u6613\uFF0C</p><p>\u533B\u9662\u91CC\u7684\u540C\u4E8B\u548C\u6211\u8BF4\uFF0C\u5956\u91D1\u9700\u8981\u9760\u5F00\u4E2D\u836F\u83B7\u53D6\uFF0C\u4E00\u5E16\u4E2D\u836F\u6709 1.5 \u63D0\u6210\uFF0C\u53E6\u5916\u5C31\u662F\u6302\u53F7\u8D39\u3002</p><p>\u53EF\u662F\u56E0\u4E3A\u5185\u5FC3\u6DF1\u5904\u5BF9\u4E2D\u533B\u836F\u7684\u62B5\u6297\uFF0C\u5B9E\u5728\u65E0\u6CD5\u8FDD\u5FC3\uFF0C\u4E00\u822C\u90FD\u662F\u60A3\u8005\u81EA\u5DF1\u8981\u6C42\u5F00\u4E2D\u836F\u624D\u4F1A\u7ED9\u60A3\u8005\u5F00\u3002</p><p>\u4E4B\u540E\u51E0\u4E2A\u6708\u5956\u91D1\u4E5F\u7EF4\u6301\u5728\u4E86\u56DB\u4E94\u767E\u5DE6\u53F3\u4E00\u4E2A\u6708\uFF0C\u601D\u6765\u60F3\u53BB\u8981\u4E0D\u7136\u8F9E\u804C\uFF0C\u53EF\u662F\u8D38\u7136\u8F9E\u804C\u4E5F\u60F3\u4E0D\u51FA\u81EA\u5DF1\u54EA\u91CC\u8FD8\u6709\u66F4\u597D\u7684\u53BB\u5904\uFF0C\u5E78\u800C\u533B\u9662\u5916\u79D1\u53D1\u5C55\u4E0D\u4F73\uFF0C\u4EBA\u624B\u7D27\u7F3A\uFF0C\u53BB\u4E86\u533B\u9662\u5916\u79D1\u6253\u517C\u804C\u3002</p><p>\u5230\u4E86\u5916\u79D1\u4EE5\u540E\uFF0C\u867D\u7136\u5DE5\u4F5C\u5F3A\u5EA6\u5927\u4E86\u5F88\u591A\uFF0C\u4F46\u662F\u4E5F\u5B66\u4E60\u5230\u4E86\u66F4\u591A\uFF0C\u5916\u79D1\u56E0\u4E3A\u6709\u5200\u9738\u4E3B\u4EFB\u7684\u5B58\u5728\uFF0C\u5E95\u4E0B\u7684\u5E74\u8F7B\u533B\u751F\u4E0A\u624B\u673A\u4F1A\u5C11\uFF0C\u6210\u957F\u5468\u671F\u6162\uFF0C\u6D41\u5931\u4E86\u5F88\u591A\u4EBA\u624D\uFF0C\u6BD4\u8F83\u6709\u60F3\u6CD5\u548C\u8FDB\u53D6\u5FC3\u7684\u5F88\u591A\u79BB\u5F00\u4E86\u533B\u9662\u53E6\u8C0B\u9AD8\u5C31\uFF0C\u5269\u4E0B\u7684\u5F88\u591A\u90FD\u662F\u6DF7\u65E5\u5B50\u7684\u4EBA\u7F62\u4E86\u3002</p><p>\u6240\u4EE5\u533B\u9662\u7684\u5916\u79D1\u4E00\u76F4\u53D1\u5C55\u4E0D\u8D77\u6765\uFF0C\u5F88\u591A\u6280\u672F\u90FD\u6CA1\u6709\u8DDF\u4E0A\uFF0C\u6CCC\u5C3F\u5916\u79D1\u7684\u624B\u672F\u4E3B\u4EFB\u4E00\u624B\u6293\uFF0C\u8154\u955C\u4E0B\u759D\u6C14\u4FEE\u8865\u672F\u4E5F\u662F\u53EA\u6709\u4E3B\u4EFB\u64CD\u5200\uFF0C\u50CF\u8840\u7BA1\u4ECB\u5165\u3001\u5185\u955C\u4E0B\u80C6\u9053\u53D6\u77F3\u672F\u90FD\u6CA1\u80FD\u5F00\u5C55\uFF0C\u843D\u540E\u4E8E\u540C\u5E02\u7684\u4EBA\u6C11\u533B\u9662\u4E00\u5927\u622A\uFF0C\u4ECA\u5E74\u540C\u6837\u4E89\u53D6\u533B\u9662\u4E09\u4E59\u7B49\u7EA7\uFF0C\u4EBA\u6C11\u533B\u9662\u8BC4\u5BA1\u901A\u8FC7\uFF0C\u4E2D\u533B\u9662\u5C31\u8BC4\u5BA1\u5931\u8D25\u4E86\uFF0C\u4E8C\u7532\u5012\u662F\u590D\u8BC4\u4FDD\u4F4F\u4E86\u3002</p><p>\u6765\u5230\u5916\u79D1\u4EE5\u540E\uFF0C\u5956\u91D1\u591A\u4E86\u4E24\u4E09\u5343\uFF0C\u751F\u6D3B\u4E0A\u7684\u7126\u8651\u5C11\u4E86\u5F88\u591A\uFF0C\u9A6C\u9A6C\u864E\u864E\u65E5\u5B50\u4E5F\u8FD8\u53EF\u4EE5\u3002</p><p>\u4ECA\u5E74\u5F00\u59CB\uFF0C\u4E2D\u836F\u63D0\u6210\u5927\u6709\u6539\u53D8\uFF0C\u6BCF\u8D34\u4E2D\u836F\u4E0D\u518D\u6709\u63D0\u6210\uFF0C\u800C\u662F\u5728\u5B8C\u6210\u4E2D\u836F\u6216\u8005\u4E2D\u6210\u836F\u5F00\u65B9\u540E\u53EF\u4EE5\u6536\u53D6\u4E2D\u533B\u95E8\u8BCA\u8FA9\u8BC1\u8BBA\u6CBB\uFF0C\u4F9D\u636E\u533B\u751F\u7684\u7B49\u7EA7\u804C\u79F0\u6709\u4E0D\u540C\u7684\u8D39\u7528\uFF0C\u4F5C\u4E3A\u4F4F\u9662\u533B\uFF0C\u6211\u7684\u6536\u8D39\u662F 10 \u5143\uFF0C\u6211\u80FD\u62FF\u5230\u591A\u5C11\u4E0D\u6E05\u695A\uFF0C\u5E94\u8BE5\u662F\u4E0D\u591A\uFF0C\u95E8\u8BCA\u7684\u5956\u91D1\u5BF9\u6BD4\u4E4B\u524D\u5C11\u4E86\u4E00\u534A\u5DE6\u53F3\u3002</p><p>\u4F46\u6211\u4E0D\u66FE\u5728\u610F\uFF0C\u56E0\u4E3A\u6211\u5728\u5916\u79D1\u80FD\u83B7\u53D6\u4E24\u4E09\u5343\u5DE6\u53F3\u7684\u5956\u91D1\uFF0C\u6240\u4EE5\u5BF9\u5F00\u4E2D\u836F\u83B7\u53D6\u5956\u91D1\u4E00\u76F4\u672A\u4E88\u652F\u6301\u3002</p><p>\u4F46\u4ECA\u5E74\u4E0B\u534A\u5E74\u5F00\u59CB\uFF0C\u533B\u9662\u5F00\u59CB\u8981\u6C42\u6211\u4EEC\u8981\u8FBE\u5230\u4E2D\u836F\u5F00\u65B9\u7387\uFF0C\u5E76\u4E14\u6BCF\u4E2A\u4EBA\u7684\u8D26\u53F7\u4E0A\u90FD\u6709\u4E2D\u836F\u5F00\u65B9\u5360\u6BD4\uFF0C\u4EE5\u6B64\u4F5C\u4E3A\u7EE9\u6548\u8003\u6838\uFF0C\u8FBE\u4E0D\u5230\u5C31\u6263\u94B1\uFF0C\u4F46\u5BF9\u4E8E\u60A3\u8005\u7684\u5747\u6B21\u8D39\u7528\u90FD\u8981\u6C42\u6BD4\u53BB\u5E74\u4F4E\uFF0C\u5982\u679C\u8D85\u8FC7 0.1%\uFF0C\u5219\u6263\u8D39 100\uFF1B\u95E8\u8BCA\u5982\u6B64\uFF0C\u75C5\u623F\u4EA6\u662F\u5982\u6B64\u4F5C\u4E3A\u5916\u79D1\uFF0C\u60A3\u8005\u7684\u4E2D\u836F\u53E3\u670D\u610F\u613F\u672C\u6765\u5C31\u4F4E\uFF0C\u52A0\u4E0A\u5916\u79D1\u60A3\u8005\u5468\u8F6C\u7387\u5FEB\uFF0C\u7ECF\u5E38\u4E2D\u836F\u6CA1\u5F00\u4E0A\uFF0C\u60A3\u8005\u5C31\u51FA\u9662\u4E86\uFF0C\u5982\u6B64\u5916\u79D1\u6210\u4E3A\u4E86\u4E2D\u836F\u8FBE\u6807\u7387\u5012\u6570\u7684\u79D1\u5BA4\u3002</p><p>\u6BCF\u4E2A\u6708\u56E0\u6B64\u6709\u56DB\u4E94\u5343\u6263\u8D39\uFF08\u597D\u50CF\u662F\u7684\uFF09\u52A0\u4E0A\u9886\u5BFC\u65BD\u538B\uFF0C\u5927\u5BB6\u90FD\u5728\u52AA\u529B\u5730\u5C3D\u91CF\u5F00\u4E2D\u836F\uFF0C\u5916\u79D1\u7684\u8001\u4E00\u6279\u533B\u751F\u591A\u662F\u897F\u533B\u51FA\u751F\uFF0C\u5BF9\u4E2D\u836F\u672C\u6765\u4E5F\u5C31\u4E0D\u611F\u5192\uFF0C\u9762\u5BF9\u538B\u529B\u53EA\u80FD\u558A\u5E74\u8F7B\u4E2D\u533B\u5E08\u5C3D\u91CF\u5F00\u4E2D\u836F\uFF0C\u4E8E\u662F\u538B\u529B\u5C31\u5230\u4E86\u6211\u8FD9\u91CC\u3002</p><p>\u6211\u4E0D\u559C\u6B22\u4E2D\u533B\u836F\uFF0C\u6211\u6000\u7591\u4E2D\u533B\u836F\u7684\u6548\u679C\uFF0C\u89C9\u5F97\u4E0D\u4EC5\u526F\u4F5C\u7528\u53EF\u7591\uFF0C\u66F4\u662F\u5BF9\u533B\u4FDD\u8D44\u91D1\u7684\u6D6A\u8D39\uFF0C\u533B\u4FDD\u8D44\u91D1\u4E00\u76F4\u514B\u6263\u7684\u5F88\u4E25\u683C\uFF0C\u4E3A\u4EC0\u4E48\u94B1\u5C31\u8981\u6D6A\u8D39\u5728\u4E2D\u533B\u836F\u8FD9\u91CC\u5462\u6700\u8FD1\u66F4\u8FC7\u5206\u4E86\uFF0C\u6240\u6709\u4F4F\u9662\u60A3\u8005\u5747\u8981\u6C42\u4E2D\u533B\u75C5\u5386\uFF0C\u53EF\u662F\u50CF\u80BE\u56CA\u80BF\u3001\u80BA\u7ED3\u8282\u3001\u8180\u80F1\u80BF\u7269\u7B49\u65E0\u75C7\u72B6\u7684\u73B0\u4EE3\u533B\u5B66\u79D1\u6280\u624B\u6BB5\u4F53\u68C0\u67E5\u51FA\u6765\u7684\u75BE\u75C5\u53C8\u5230\u54EA\u91CC\u627E\u4E2D\u533B\u8BCA\u65AD\u4F55\u6CBB\u7597\u65B9\u6848\u5462\uFF0C\u5982\u679C\u4E0D\u5199\u4E2D\u533B\u75C5\u5386\uFF0C\u6863\u6848\u5BA4\u62D2\u6536\u75C5\u5386\uFF0C\u6863\u6848\u5BA4\u4E0D\u6536\u75C5\u5386\uFF0C\u53C8\u662F\u53EF\u53E3\u533B\u751F\u7684\u5956\u91D1\u3002</p><p>\u6211\u73B0\u5728\u5185\u5FC3\u53D7\u5230\u5F88\u5927\u7684\u56F0\u6270\uFF0C\u5173\u4E8E\u751F\u5B58\u548C\u81EA\u6211\u610F\u613F\uFF0C\u7763\u5DE5\u6211\u4E0D\u7406\u89E3\u4E3A\u4EC0\u4E48\u56FD\u5BB6\u4F1A\u5927\u529B\u6276\u6301\u4E2D\u533B\u836F\uFF0C\u533B\u9662\u7ADF\u201C\u903C\u826F\u4E3A\u5A3C\u201D\u5230\u8FD9\u6837\u5417\u3002</p><p>\u533B\u9662\u660E\u5E74\u8981\u642C\u65B0\u9662\u4E86\uFF0C\u4E3B\u6253\u62DB\u724C\u662F***\u4E2D\u533B\u836F\u6587\u5316\u7814\u7A76\u4E2D\u5FC3\uFF0C\u66F4\u6709\u7814\u7A76\u7384\u5B66\u7684\u5473\u9053\u4E86\uFF0C\u4E00\u70B9\u4E5F\u4E0D\u7B26\u5408\u73B0\u4EE3\u533B\u5B66\u53D1\u5C55\u8FD9\u4E48\u5FEB\u7684\u4ECA\u5929\uFF0C\u6700\u8FD1\u533B\u9662\u5728\u6536\u96C6\u804C\u5DE5\u7684\u610F\u89C1\uFF0C\u7763\u5DE5\uFF0C\u6211\u5E94\u8BE5\u8D81\u7740\u8FD9\u6B21\u673A\u4F1A\u600E\u4E48\u548C\u533B\u9662\u63D0\u5173\u4E8E\u4E2D\u533B\u548C\u4E2A\u4EBA\u610F\u613F\u8FDD\u80CC\u7684\u4E8B\u60C5\u5462\uFF0C\u5BF9\u4E8E\u6211\u4EEC\u533B\u9662\u5916\u79D1\u53D1\u5C55\u73B0\u72B6\u60A8\u53C8\u6709\u4EC0\u4E48\u9AD8\u89C1\u5462\u3002</p>',21),r=[c];function i(n,o,_,d,l,h){return a(),p("div",null,r)}const m=e(s,[["render",i]]);export{f as __pageData,m as default};
